import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import dateConverter from '../../utils/utils'
import '../../styles/blog_preview.css'

class BlogPreviewItem extends Component {
	constructor(props) {
		super(props);

		this.state = {
			item:this.props.item
		};
	}
	render() {
		let date = dateConverter(this.state.item.published_time)
		console.log(date)
		return (
			<div>
				<Link to={this.state.item.unique_url} style={{color:"black", textDecoration:"none"}}>
					<Card style={{ width: '18rem' }} className="previewItem">
						<Card.Body>
							<Card.Title>{this.state.item.post_title}</Card.Title>
							<Card.Text>{this.state.item.excerpt}</Card.Text>
							<Button variant="primary" href={this.state.item.unique_url}>
								View
							</Button>
						</Card.Body>
						<Card.Footer className="text-muted">
						Published: {this.state.item.published_time + date}
						</Card.Footer>
					</Card>
				</Link>
			</div>
		);
	}
}

BlogPreviewItem.propTypes = {
	item: PropTypes.object.isRequired
}

export default BlogPreviewItem;
