import React, { Component } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import '../../styles/about.css'

class AboutMe extends Component {
	render() {
		return (
			<div>
				<Row className="justify-content-md-center">
					<Col>
						<h1 style={{textAlign:"center"}}><b>Hello There!</b></h1>
					</Col>
				</Row>
				<Row className="justify-content-md-center">
					<Col>
						<img style={{alignSelf:"flex-end"}} src={"https://i.redd.it/v9ch8s7hhl501.jpg"} alt={""} />
					</Col>
					<Col>
						<p className="aboutText">I am Nigel, and thank you for visiting my website!</p>
					</Col>
				</Row>
				<br />
				<p className="aboutText">I graduated from Washington State University with
				   with a B.S in Computer Science. I am currently looking to start
				   my career in the tech industry.
				</p>
				<img src={"https://cdn.worldvectorlogo.com/logos/washington-state-cougars-2.svg"} alt={""} />
				<p className="aboutText">I am very passionate about technology and am always excited for new 
				   opportunities to learn more. 
				</p>
				<audio controls autoplay ref="audio_tag" src="obi-wan-hello-there.mp3" />
			</div>
		);
	}
}



export default AboutMe;
