import React, { Component } from 'react';

import DropdownButton from 'react-bootstrap/DropdownButton';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import axios from 'axios';

class LeaderboardContainer extends Component {
	constructor(props) {
		super(props);

		this.state = {
			scores: [],
			isLoading: true,
			game_name: this.props.match.params.game_name
		}
	};

	componentWillMount() {
		axios.get("https://nigelhaney.me/api/game/" + this.state.game_name).then(
			res => {
				this.setState({scores: res.data.data,
							   isLoading: false});
			});
	};

	gameTitleComponent() {
		if (this.state.game_name === "fish") {
			return (<h2>High Scores: Stay Off The Line!</h2>);
		}
		else {
			return (<h2>Leaderboard not found</h2>);
		}
	};

	render() {
		return (
			<Container>
				<Row>
					<Col>
						<h1 style={{textAlign:"center"}}>Game Leaderboards</h1>
					</Col>
				</Row>
				<Row>
					<Col>
						{this.gameTitleComponent()}
					</Col>

				</Row>

			</Container>
		);
	}
}

export default LeaderboardContainer;
