import React, { Component } from 'react';
import axios from 'axios';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class MailListContainer extends Component {
	render() {
		return (
			<Container>
				<Row>
					<Col>
						<h4>Sign up for my mailing list!</h4>
					</Col>
					<Col>
						<p>You will be notified whenever I make a new blog post</p>
					</Col>
				</Row>
				<Row>
					<Col>
						<Form>
							<Form.Control type="email" placeholder="Enter your email here" />
							<Form.Text className="text-muted">
								No spam, I promise. You may unsubscribe at any time.
							</Form.Text>
						</Form>
					</Col>
					<Col>
						<Button variant="primary" type="submit">
							Sign up!
						</Button>
					</Col>
				</Row>
			</Container>
		);
	}
}

export default MailListContainer;
