import React, { Component } from 'react';
import axios from 'axios';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class MailListContainer extends Component {
	constructor(props) {
		super(props);

		this.state = {
			email: "",
			isSubmitting: false,
			hasSubmitted: false
		}

		this.handleChange = this.handleChange.bind(this);
		this.subscribe = this.subscribe.bind(this);
	}

	handleChange(event) {
		this.setState({email: event.target.value});
	}

	subscribe(email) {
		this.setState({isSubmitting: true});
		axios.post("https://nigehaney.me/api/mail/subscribe",
							{ "email": this.state.email }).then(
							res => {
								console.log(res);
								if(res.data.code == 200) {

								}
		});
	}

	render() {
		return (
			<Container>
				<Row>
					<Col>
						<h4>Sign up for my mailing list!</h4>
						<p>You will be notified whenever I make a new blog post</p>
					</Col>
					<Col>

					</Col>
				</Row>
				<Row>
					<Col>
						<Form>
							<Form.Control type="email" placeholder="Enter your email here" onChange={this.handleChange} />
							<Form.Text className="text-muted">
								No spam, I promise. You may unsubscribe at any time.
							</Form.Text>
						</Form>
					</Col>
					<Col>
						<Button variant="primary" onClick={this.subscribe}>
							Sign up!
						</Button>
					</Col>
				</Row>
			</Container>
		);
	}
}

export default MailListContainer;
