import React, { Component } from 'react';

import Dropdown from 'react-bootstrap/Dropdown';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import axios from 'axios';

import HighScoreTable from './HighScoreTable';

class LeaderboardContainer extends Component {
	constructor(props) {
		super(props);

		this.state = {
			scores: [],
			isLoading: true,
			game_name: this.props.match.params.game_name
		}
	};

	componentWillMount() {
		axios.get("https://nigelhaney.me/api/game/" + this.state.game_name).then(
			res => {
				this.setState({scores: res.data.data,
							   isLoading: false});
			});
	};

	gameTitleComponent() {
		if (this.state.game_name === "fish") {
			return (<h2 style={{textAlign:"center"}}>High Scores: Stay Off The Line!</h2>);
		}
		else {
			return (<h2 style={{textAlign:"center"}}>Leaderboard not found</h2>);
		}
	};

	dropDownComponent() {
		if (this.state.game_name === "fish") {
			return(
					<Dropdown style={{float:"right"}}>
							<Dropdown.Toggle>
								Select Leaderboard
							</Dropdown.Toggle>
							<Dropdown.Menu>
								<Dropdown.Item disabled={true} href="/games/scores/fish">Stay Off The Line</Dropdown.Item>
							</Dropdown.Menu>
						</Dropdown>
				)
		}
		else {
			return(
					<Dropdown style={{float:"right"}}>
							<Dropdown.Toggle>
								Select Leaderboard
							</Dropdown.Toggle>
							<Dropdown.Menu>
								<Dropdown.Item href="/games/scores/fish">Stay Off The Line</Dropdown.Item>
							</Dropdown.Menu>
						</Dropdown>
				)
		}
	}

	render() {
		return (
			<Container>
				<Row>
					<Col>
						<h1 style={{textAlign:"center"}}>Game Leaderboards</h1>
					</Col>
				</Row>
				<Row>
					<Col>
						{this.gameTitleComponent()}
						<Dropdown style={{float:"right"}}>
							<Dropdown.Toggle>
								Select Leaderboard
							</Dropdown.Toggle>
							<Dropdown.Menu>
								<Dropdown.Item href="/games/scores/fish">Stay Off The Line</Dropdown.Item>
							</Dropdown.Menu>
						</Dropdown>
					</Col>
				</Row>
				<hr />
				<Row>
					<HighScoreTable data={this.state.scores}/>
				</Row>

			</Container>
		);
	}
}

export default LeaderboardContainer;
