import React, { Component } from 'react';
import BlogPreviewItem from './BlogPreviewItem';
import axios from 'axios';
import '../../styles/blog_preview.css'
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


class BlogPreview extends Component {

	constructor(props) {
		super(props);

		this.state = {
			posts: [],
			isLoading: true
		};
	}

	componentDidMount(){
		axios.get("https://nigelhaney.me/api/blog/posts").then(res => {
			console.log(res);
			this.setState({posts: res.data.data});
		});
	}

	render() {
		return(
			<Container className="d-flex flex-row flex-wrap align-items-start justify-content-sm-center">
				{
					!this.state.isLoading &&
					this.state.posts.map((item) => {
						return(
								<BlogPreviewItem key={item.post_id} item={item} />
						)
					})
				}
			</Container>
		)
	}
}

export default BlogPreview;
