import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

class BlogPreviewItem extends Component {
	render() {
		return (
			<div>
				<Card style={{ width: '18rem' }}>
				<Card.Body>
					<Card.Title>{this.props.item.post_title}</Card.Title>
					<Card.Text>{this.props.item.excerpt}</Card.Text>
					<Button variant="primary" href={this.props.item.unique_url}>
						View
					</Button>
				</Card.Body>
				<Card.Footer className="text-muted">
				Published {this.props.item.published_time}
				</Card.Footer>
				</Card>
				<NavLink to={this.props.item.unique_url}>
					<div style={navigationStyle}>
						<h2>{ this.props.item.post_title}</h2>
						<h3>{ this.props.item.excerpt }</h3>
						<p>Comments: {this.props.item.comment_count}</p>
						<p>Published Time: {this.props.item.published_time}</p>
					</div>
				</NavLink>
			</div>
		);
	}
}

const navigationStyle = {
	backgroundColor: 'green'
}

BlogPreviewItem.propTypes = {
	item: PropTypes.object.isRequired
}

export default BlogPreviewItem;
