import React, { Component } from 'react';
import startGame from './gameController';

class FishGame extends Component {
	componentDidMount() {
		const script = document.createElement("script");
		script.src = "gameController.js";
		script.async = true;

		document.body.appendChild(script);
	}

	render() {
		return (
			<div>
				<div className="row">
					<div className="column1">
						<div id="gameColumn">
							<h1>Stay Off the Line!</h1>
						</div>
						<div id="highScores">
							<br />
							<h3>High Scores</h3>
							<table id="scoreTable"></table>
						</div>
					</div>
					<div className="column2">
						<div id="howTo">
							<br /><br />
							<h2>How to play:</h2>
							<p> 
								Use <b>arrow keys</b> or <b>WASD</b> to move your fish around the ocean.<br />
								Press <b>space</b> to use a boost. To get more boosts, eat more worms!<br />
								Don't starve, and most importantly, <b>Stay Off the Line!</b><br />
							</p>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default FishGame;
