import React, { Component } from 'react';

class Comment extends Component {
	render() {
		return (
			<div>
				<p><b>{this.props.item.author}</b> at <i>{this.props.item.published_time}</i></p>
				<p>{this.props.item.content}</p>
			</div>
		);
	}
}


export default Comment;
