import React, { Component } from 'react';
import axios from 'axios';
import CommentContainer from './CommentContainer';

class BlogPost extends Component {

	constructor(props) {
		super(props);

		this.state = {
			post_info: []
		};
	}

	componentDidMount(){
		console.log(this.props.match);
		const post_title = this.props.match.params.post_title;
		axios.get("http://nigelhaney.me/api/blog/posts/" + post_title).then(res => {
			this.setState({post_info: res.data.data});
		});
	}

	render() {
		console.log(this.state.post_info);
		return (
			<div>
				<div>
					<h1>{this.state.post_info.post_title}</h1>
					<h3>Published: {this.state.post_info.published_time}</h3>
					<p>{this.state.post_info.content}</p>
				</div>
				<div>
					<CommentContainer post_id={this.state.post_info.post_id} />
				</div>
			</div>
		);
	}
}

export default BlogPost;
