function formatDate(date) {
	var cur_date = date.getDate();
	cur_date = cur_date < 10 ? "0" + cur_date : cur_date;

	var cur_month = date.getMonth() + 1;
	cur_month = cur_month < 10 ? "0" + cur_month : cur_month;

	let cur_year = date.getFullYear();

	var cur_hour = date.getHours();
	var cur_min = date.getMinutes();
	cur_min = cur_min < 10 ? '0' + cur_min : cur_min;
	var ampm = cur_hour >= 12 ? 'pm' : 'am';

	cur_hour = cur_hour % 12;
	cur_hour = cur_hour ? cur_hour : 12;

	let strTime = cur_hour + ':' + cur_min + ' ' + ampm;

	return cur_month + "/" + cur_date + "/" + cur_year + " at " + strTime;
}

function dateConverter(time_stamp) {
	console.log(time_stamp);
	let date = new Date(
		Date.parse(time_stamp + ' UTC')
	);

	return formatDate(date);
};

export default dateConverter;
