import React, { Component } from 'react';
import axios from 'axios';
import { Link }  from 'react-router-dom';
import CommentContainer from './CommentContainer';
import MailListContainer from './MailListContainer';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


class BlogPost extends Component {

	constructor(props) {
		super(props);

		this.state = {
			post_info: [],
			isLoading: true,
			hasErrored: false
		};
	}

	componentWillMount(){
		console.log(this.props.match);
		const post_title = this.props.match.params.post_title;
		axios.get("https://nigelhaney.me/api/blog/posts/" + post_title)
		.then(res => {
			this.setState({post_info: res.data.data,
						   isLoading: false});
		})
		.catch(res => {
			console.log(res.response)
			this.setState({isLoading: false,
						   hasErrored: true,
						   post_info: res.response.data});
		});
	}

	render() {
		console.log(this.state.post_info)
		return (
				<Container>
					{this.state.isLoading && 
						<h1 style={{textAlign:"center"}}>Loading...</h1>
					}
					{this.state.hasErrored && 
						<Container>
							<h1 style={{textAlign:"center"}}>{this.state.post_info.message}</h1>
							<Link to="/blog">
								<h3 style={{textAlign:"center"}}>Click here to return to blog</h3>
							</Link>
						</Container>
					}
					{!this.state.isLoading && !this.state.hasErrored &&
						<Container>
							<Row className="justify-content-md-center">
								<h1 style={{textAlign:"center"}}>{this.state.post_info.post_title}</h1>
							</Row>
							<Row className="justify-content-md-center">
								<h3 style={{textAlign:"center"}}>Published: {this.state.post_info.published_time}</h3>
							</Row>
							<Row >
								<h4 style={{textAlign:"center"}}>{this.state.post_info.last_edited && "Last edited: " + this.state.post_info.last_edited}</h4>
							</Row>
							<Row>
								<p style={contentStyle}>{this.state.post_info.content}</p>
							</Row>
							<Row>
								<CommentContainer post_id={this.state.post_info.post_id} />
							</Row>
							<Row>
								<MailListContainer />
							</Row>
						</Container>
					}
				</Container>
		);
	}
}

const contentStyle = {
	"border":"1px solid black",
	"padding":"20px",
}

export default BlogPost;
