function dateConverter(time_stamp) {
	var newDate = new Date(time_stamp.getTime()+time_stamp.getTimezoneOffset()*60*1000);

    var offset = time_stamp.getTimezoneOffset() / 60;
    var hours = time_stamp.getHours();

    newDate.setHours(hours - offset);

    return newDate; 
};

export default dateConverter;
