import React, { Component } from 'react';
import Navigation from './components/Navigation';
import BlogPreview from './components/blog/BlogPreview';
import BlogPost from './components/blog/BlogPost';
import AboutMe from './components/about/AboutMe';
import GamePreviewContainer from './components/games/GamePreviewContainer';
import LeaderboardContainer from './components/games/LeaderboardContainer';
import ExperienceContainer from './components/experience/ExperienceContainer';
import HomePage from './components/home/HomePage';
import Header from './components/Header';
import Footer from './components/Footer';

// Redux testing
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { updateUser } from './actions/userActions';

// Router testing
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import './App.css';

import {CircleArrow as ScrollUpButton} from 'react-scroll-up-button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


class App extends Component {
	state = {
		banner_items: [
			{
				id: 0,
				page_title: "Blog"
			},
			{
				id: 1,
				page_title: "Resume"
			},
			{
				id: 2,
				page_title: "Games"
			},
			{
				id: 3,
				page_title: "Projects"
			},
			{
				id: 4,
				page_title: "More"
			}
		]
	}

	constructor(props) {
		super(props);

		this.onUpdateUser = this.onUpdateUser.bind(this);
	}

	onUpdateUser(event) {
		this.props.onUpdateUser(event.target.value);
	}


	render() {
		// console.log(this.state.banner_items);
		return (
			<BrowserRouter>
				<div className="main-container">
					<Header />
					<Navigation />
					<Switch>
						<Route path="/" exact component={HomePage} />
						<Route path="/blog" exact>
							<Container>
								<h1 style={{textAlign:"center"}}><b>Nigel's Blog</b></h1>
								<hr />
								<BlogPreview />
							</Container>
						</Route>
						<Route path="/blog/:post_title" component={BlogPost} />
						<Route path="/about">
							<AboutMe />
						</Route>
						<Route exact path="/games" component={GamePreviewContainer} />
						<Route exact path="/games/scores/:game_name" component={LeaderboardContainer} />
						<Route path="/experience" component={ExperienceContainer} />
					</Switch>
					<ScrollUpButton />
					<Footer />
				</div>
			</BrowserRouter>
		);
	}
}

const mapStateToProps = (state, props) => {
	return {
		blogs: state.blogs,
		user: state.user,
		userPlusProp: `${state.user} ${props.aRandomProps}`
	}
};

const mapActionsToProps = (dispatch, props) => {
	console.log(props);

	return bindActionCreators({
		onUpdateUser: updateUser
	}, dispatch);

};

// const mergeProps = (propsFromState,
// 	propsFromDispatch, ownProps) => {
// 	console.log(propsFromState, propsFromDispatch, ownProps);
// 	return {};
// }

export default connect(mapStateToProps, mapActionsToProps)(App);
