import React, { Component } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container'
import '../../styles/about.css'

class AboutMe extends Component {
	render() {
		return (
			<div>
				<Container>
					<Row>
						<Col style={divStyle}>
							<h1 style={{textAlign:"center"}}><b>Hello There!</b></h1>
						</Col>
					</Row>
					<Row>
						<Col style={divStyle}>
							<img height={250} width={300} style={{alignSelf:"flex-end"}} src={"https://i.redd.it/v9ch8s7hhl501.jpg"} alt={""} />
						</Col>
						<Col style={divStyle}>
							<p className="aboutText">I am Nigel, and thank you for visiting my website!</p>
						</Col>
					</Row>
					<hr />
					<Row>
						<Col style={divStyle}>
							<p className="aboutText">I am a recent graduate (December, 2018) from Washington State University with
						   with a B.S in Computer Science. I am currently for my first job to start
						   my career in the tech industry. I am very passionate about technology and am always excited for new 
					   	opportunities to learn more. 
							</p>
						</Col>
						<Col>
							<img height={250} width={300} src={"https://cdn.worldvectorlogo.com/logos/washington-state-cougars-2.svg"} alt={""} />
						</Col>
					</Row>
				</Container>
				<audio autoPlay ref="audio_tag" src={require("../../audio/obi-wan-hello-there.mp3")} />
			</div>
		);
	}
}

const divStyle = {
	//border: "1px solid black"
}



export default AboutMe;
